import React from 'react';
import './App.css';

function App() {
  return (
    <>
      <h1>React Context Api </h1>
      <p>
        React Context Api allows you to easily access data at different levels of the component tree, without passing props to every levels.
      </p>
    </>
  );
}

export default App;
