import React, { Component } from 'react';

class ProductList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            products: []
        }
    }//end of constructor

    componentDidMount() {
        const apiUrl = 'http://localhost/ci-api-for-react/reactapi/products';

        fetch(apiUrl)
            .then(res => res.json())
            .then((result) => {
                this.setState({
                    products: result
                })
            }, (error) => {
                this.setState({ error });
            }
            )

    }

    render() {
        const { error, products } = this.state;
        if (error) {
            return <div>Error: {error.message}</div>
        }
        return (
            <>
                <h2>Product List from Codeigniter  API</h2>
                <Table>

                </Table>
            </>
        )
    }
}

export default ProductList;
