import { Card, Table, Typography } from '@material-ui/core';
import React, { Component } from 'react';

class ProductList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            products: []
        }
    }//end of constructor

    componentDidMount() {
        const apiUrl = 'http://localhost/ci-api-for-react/reactapi/products';

        fetch(apiUrl)
            .then(res => res.json())
            .then((result) => {
                this.setState({
                    products: result
                })
            }, (error) => {
                this.setState({ error });
            }
            )

    }

    render() {
        const { error, products } = this.state;
        if (error) {
            return <div>Error: {error.message}</div>
        }
        return (
            <>
                <Typography varient="h2" style={{ textAlign: "center" }} >Product List from Codeigniter  API</Typography>
                <Card>
                    <Table>
                        <thead>
                            <tr>
                                <th>#ID</th>
                                <th>Product Name</th>
                                <th>SKU</th>
                                <th>Price</th>
                            </tr>
                        </thead>
                        <tfoot>
                            <tr>
                                <th>#ID</th>
                                <th>Product Name</th>
                                <th>SKU</th>
                                <th>Price</th>
                            </tr>
                        </tfoot>
                        <tbody>
                            {
                                products.map((product) => (
                                    <tr>
                                        <td>{product.id}</td>
                                        <td>{product.product_name}</td>
                                        <td>{product.sku}</td>
                                        <td>{product.price}</td>
                                    </tr>
                                ))
                            }
                        </tbody>
                    </Table>
                </Card>
            </>
        )
    }
}

export default ProductList;
