import React, { useState } from 'react';
import './TwoInputFields.css';
const TwoInputFields = () => {
    const [name, setName] = useState("");
    const [fullname, setFullName] = useState();

    const InputEvent = (event) => {
        setName(event.target.value);
    }

    const onSubmit = () => {
        setFullName(name);
    }
    return (
        <>
            <div className="secondFormDiv">
                <p>Controlled Component : - In controlled Component, form data is handled by a React Component.
                The alternative is uncontrolled components , where form data is handled by the Dom iteself.
            </p>
                <h1>Hello {fullname}</h1>
                <input type="text"
                    placeholder="Enter your Name"
                    onChange={InputEvent}
                    value={name}
                />
                <button onClick={onSubmit}>Click Me <span role="img" aria-label="lock">üîê</span></button>
            </div>
        </>
    );
}

export default TwoInputFields;
