import React, { useState } from 'react';
import '../../../src/components/Events/EventBackGround.css';

const EventsBackGround = () => {
    const purple = "#8244ad";
    const [bg, setBg] = useState(purple);
    const ChangeBg = () => {
        let red = "red";
        setBg(red)
    }
    const backToNormal = () =>{
        let purple = "#8244ad"
        setBg(purple)
    }

    return (
        <>
            <div className="event_main_content" style={{ backgroundColor: bg }}>
                <button className="background_button" onClick={ChangeBg} onDoubleClick={backToNormal} >Change BackGround Color</button>
            </div>
        </>
    );
}

export default EventsBackGround;
