import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [num, setNum] = useState(0);

  //useEffect always need a function 
  useEffect(() => {
    alert('i am clicked')
  }
  )

  return (
      <>  
        <button onClick = {() =>{ setNum(num+1)}}>Click me {num}</button>
      </>
    );
}

export default App;
